export async function sendConfirmationEmail(guest: Guest, answer: RsvpAnswer): Promise<void> {
  if (!answer.email) {
    console.log(`No email provided for RSVP (guest ID ${guest.id})`);
    return;
  }

  const client = ses.createClient(config.sesConfig);

  console.log('Sending Email', guest, answer);

  await client.sendEmail({
    to: answer.email,
    // to: 'lessing.paul@gmail.com',
    bcc: 'rsvp@lamp.wedding',
    from: 'Lam and Paul <rsvp@lamp.wedding>',
    subject: `Response Received`,
    message: answer.isAttending ? getConfirmYesEmail(guest, answer) : getConfirmNoEmail(guest, answer),
  });
}

