function name({ firstName, lastName }: { firstName: string, lastName: string }): string {
  return `${ firstName } ${ lastName }`;
}

function joinNames(guests: Guest[]): string {
  const names = guests.map(name);
  if (names.length <= 2) {
    return names.join(' and ');
  } else {
    return names.slice(0, names.length - 1).join(', ') + ' and ' + names[names.length - 1];
  }
}
